public class booleandatatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//logical - boolean datatype
		//it returns only true or false value
		//default value is "false"
		//memory size - none
		
		boolean isAccept = true;
		boolean isActivate = false;
		boolean isRight = true;
		boolean isWrong = false;

		
		System.out.println("Accepted :- "+isAccept);
		System.out.println("Activated :-"+isActivate);
		System.out.println("Righted :-"+isRight);
		System.out.println("Wronged :-"+isWrong);
	}

}